package sw230414_test;

public class ForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<5;i++) { //5회전되는 반복문
			//ㅑ=0, ㅑ=1, ㅑ=2, ㅑ=3, ㅑ=4
			System.out.println("안녕!!");
		}
		int j =10;
		
		while(j<5) {
			System.out.println("안녕!!");
			j++;
		
		}
	}

}
